import React from 'react';
import './Weather.css';
import SavedActivity from './SavedActivity';

function Share({ activities }) {
  const shareUrl = `https://www.facebook.com/sharer/sharer.php?u=&quote=${encodeURIComponent(
    activities && activities.join('\n') 
  )}`;

  return (
    <div className='share-container'>
      <h2>Share</h2>
      <p>Share your selected activities with your friends:</p>
      <a href={shareUrl} target="_blank" rel="noopener noreferrer">Share on Facebook</a>
    </div>
  );
}

function Weather() {
  return (
    <div>
      <SavedActivity />
      <Share activities={[{ id: 1, name: 'Go for a hike', weatherType: 'Clear' }, { id: 2, name: 'Go to the beach', weatherType: 'Clear' }, { id: 3, name: 'Go skiing', weatherType: 'Snow' }, { id: 4, name: 'Go ice skating', weatherType: 'Snow' }, { id: 5, name: 'Go to the movies', weatherType: 'Rain' }, { id: 6, name: 'Play board games', weatherType: 'Rain' }]} />
    </div>
  );
}

export default Weather;
